import React, { useEffect, Fragment, useState } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { getData, getSiteImages, getYoutubeVideoId } from "./helpers/api";
import './App.css';
import './Main.css';
import Footer from "./components/includes/footer";
import Header from "./components/includes/header";
import Home from "./components/pages/home";

function App() {
  const [loading, setLoading] = useState(false);
  const [state, setState] = useState({
    careerPopup: false,
    publisherPopup: false,
    contactPopup: false,
  });
  useEffect(() => {
    getData("home").then((data) => {
      setState({
        ...state,
        content: data.content,
        metatags: data.metatags,
        services: data.services,
        casestudies: data.casestudies,
        right_faqs: data.right_faqs,
        left_faqs: data.left_faqs,
        categories: data.categories,
        site_settings: data.site_settings
      });
      setLoading(true);
    });
  }, []);
  function showCareerPopup() {
    setState({ ...state, careerPopup: true });
  }
  function closeCareerPopup() {
    setState({ ...state, careerPopup: false });
  }
  function showPublisherPopup() {
    setState({ ...state, publisherPopup: true });
  }
  function closePublisherPopup() {
    setState({ ...state, publisherPopup: false });
  }
  function showContactPopup() {
    setState({ ...state, contactPopup: true });
  }
  function closeContactPopup() {
    setState({ ...state, contactPopup: false });
  }
  if (loading === false) return <div id="loading"> <img src={getSiteImages('/images/loading.gif')} alt="Loading" /></div>;
  return (

    <Router>
      <Fragment>
        <Header settings={state.site_settings} showPopup={showCareerPopup} showPublisherPopup={showPublisherPopup} showContactPopup={showContactPopup} />
        <Switch>
          <Route
            exact
            path="/"
            // component={Home}

            render={props => <Home content={state} />}
          />

        </Switch>
        <Footer settings={state.site_settings} />
      </Fragment>
      {
        (state.publisherPopup === true)
          ?
          <section class="popup" data-popup="become" style={{ display: "block" }}>
            <div class="table_dv">
              <div class="table_cell">
                <div class="contain">
                  <div class="_inner">
                    <button type="button" class="x_btn" onClick={closePublisherPopup}></button>
                    <h4>Become a publisher</h4>
                    <p>Lumicc is always looking for new vendors to join our marketplace.
                      If your website or physical location is driving over 500+ daily visitors, please get in touch to learn how we can help you further monetize your assets.</p>
                    <form action="" method="POST">
                      <div class="form_row row">
                        <div class="col-xs-12">
                          <h6 class="require">Full Name</h6>
                          <div class="form_blk">
                            <input type="text" name="" id="" class="input" placeholder="eg: John Wick" />
                          </div>
                        </div>
                        <div class="col-xs-12">
                          <h6 class="require">Email Address</h6>
                          <div class="form_blk">
                            <input type="text" name="" id="" class="input" placeholder="eg: sample@gmail.com" />
                          </div>
                        </div>
                        <div class="col-xs-12">
                          <h6 class="require">Phone Number</h6>
                          <div class="form_blk">
                            <input type="text" name="" id="" class="input" placeholder="eg: 092 1234 567" />
                          </div>
                        </div>
                        <div class="col-xs-12">
                          <h6>Comments</h6>
                          <div class="form_blk">
                            <textarea name="" id="" class="input" placeholder="How can we help?"></textarea>
                          </div>
                        </div>
                      </div>
                      <div class="btn_blk form_btn text-center">
                        <button class="site_btn lg long purple">Submit</button>
                      </div>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </section>
          :
          ""
      }
      {
        (state.careerPopup === true)
          ?
          <section class="popup" data-popup="careers" style={{ display: "block" }}>
            <div class="table_dv">
              <div class="table_cell">
                <div class="contain">
                  <div class="_inner">
                    <button type="button" class="x_btn" onClick={closeCareerPopup}></button>
                    <h4>Careers</h4>
                    <p>We're looking for talented people to join our team! If you're a marketer, graphic designer, customer support specialist, or offer similar solutions within the media space, drop us a line. We'd love to hear from you.</p>
                    <form action="" method="POST">
                      <div class="form_row row">
                        <div class="col-xs-12">
                          <h6 class="require">Full Name</h6>
                          <div class="form_blk">
                            <input type="text" name="" id="" class="input" placeholder="eg: John Wick" />
                          </div>
                        </div>
                        <div class="col-xs-12">
                          <h6 class="require">Email Address</h6>
                          <div class="form_blk">
                            <input type="text" name="" id="" class="input" placeholder="eg: sample@gmail.com" />
                          </div>
                        </div>
                        <div class="col-xs-12">
                          <h6 class="require">Phone Number</h6>
                          <div class="form_blk">
                            <input type="text" name="" id="" class="input" placeholder="eg: 092 1234 567" />
                          </div>
                        </div>
                        <div class="col-xs-12">
                          <h6>Comments</h6>
                          <div class="form_blk">
                            <textarea name="" id="" class="input" placeholder="How can we help?"></textarea>
                          </div>
                        </div>
                      </div>
                      <div class="btn_blk form_btn text-center">
                        <button class="site_btn lg long purple">Submit</button>
                      </div>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </section>
          :
          ""
      }
    </Router>
  );
}

export default App;
