import React, { Fragment, useState } from 'react'
import { getSiteImages, getServerImage, getData } from "../../../helpers/api";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
let settings = {
    dots: false,
    infinite: true,
    speed: 1000,
    slidesToShow: 3,
    slidesToScroll: 1,
    autoplaySpeed: 2000,
    autoplay: true,
    adaptiveHeight: true,
    arrows: false
};
export default function CaseStudies(props) {
    // console.log(props.casestudies);
    const [state, setState] = useState({
        casestudies: props.casestudies,
        c_id: null,
        casePopup: false,
        case_study: null
    });
    function getCaseStudies(c_id) {
        getData("get-case-studies", c_id).then((data) => {
            console.log(data);
            setState({ ...state, c_id: c_id, casestudies: data.casestudies });
        });
    }
    function showPopup(case_study) {
        setState({ ...state, casePopup: true, case_study: case_study });
    }
    function closePopup() {
        setState({ ...state, casePopup: false, case_study: null });
    }
    return (
        <section id="case">
            <div className="wrapper">
                <div className="contain">
                    <div className="top_head">
                        <h2 className="heading">{props.heading} </h2>
                        <ul className="tab_list">
                            {
                                (props.categories != undefined && props.categories.length > 0) ?
                                    props.categories.map((category, index) => (
                                        <li className={(index == 0 && state.c_id == null) ? "active" : (state.c_id === category.id) ? 'active' : ''} key={index} onClick={() => getCaseStudies(category.id)}><a href="javascript:void(0)">{category.name}</a></li>
                                    ))
                                    :
                                    ""
                            }

                        </ul>
                    </div>
                    <div id="slick-case" className="slick-carousel">
                        {
                            (state.casestudies != undefined && state.casestudies.length > 0) ?
                                <Slider {...settings}>
                                    {
                                        state.casestudies.map((case_study, c_index) => (
                                            <div className="item" key={c_index}>
                                                <div className="case_blk">
                                                    <button className="expand pop_btn" data-popup="case" onClick={() => showPopup(case_study)}><img src={getSiteImages("images/icon-expand.svg")} alt="" /></button>
                                                    <div className="fig" style={{ backgroundImage: "url('" + getServerImage('uploads/casestudies/', case_study.image, 'thumb_') + "')" }}></div>
                                                    <div className="txt">
                                                        <h5>{case_study.title}</h5>
                                                        <div dangerouslySetInnerHTML={{ __html: case_study.details }} />
                                                    </div>
                                                </div>
                                            </div>
                                        ))
                                    }

                                </Slider>
                                :
                                <div className='alert alert-danger'>No case studies found yet!</div>
                        }
                    </div>
                </div>
            </div>
            <img src={getSiteImages("images/Painting Paine Ill  1.png")} alt="" />

            {
                (state.casePopup === true && state.case_study !== null) ?
                    <div class="popup lg" data-popup="case" style={{ display: "block" }}>
                        <div class="table_dv">
                            <div class="table_cell">
                                <div class="contain">
                                    <div class="_inner">
                                        <button type="button" class="x_btn"></button>
                                        <div class="img_blk">
                                            <img src={getServerImage('uploads/casestudies/', state.case_study.image, 'thumb_')} alt={state.case_study.title} />
                                        </div>
                                        <div class="data">
                                            <h5>Campaign Types</h5>
                                            <span class="site_btn md simple round">{state.case_study.type}</span>
                                            <div class="br"></div>
                                            {
                                                (state.case_study.services.length > 0) ?
                                                    <Fragment>
                                                        <h5>Campaign Management case_studys</h5>
                                                        <div class="btn_blk">
                                                            {
                                                                state.case_study.services.map((case_study, c_index) => (
                                                                    <span class="site_btn md simple round">Conversion Rated Optmization</span>
                                                                ))
                                                            }
                                                        </div>
                                                    </Fragment>
                                                    :
                                                    ""
                                            }
                                            <div class="br"></div>
                                            <h5>Project Description</h5>
                                            <p>Professionally procrastinate user-centric potentialities and market-driven total linkage. Phosfluorescently leverage existing team driven markets before top-line experiences. Synergistically maximize multimedia based total linkage before B2B growth strategies. Efficiently envisioneer collaborative expertise vis-a-vis focused customer case_study. Dynamically envisioneer pandemic strategic theme areas after end-to-end solutions.</p>
                                            <p>Proactively develop resource-leveling processes through out-of-the-box intellectual capital. Uniquely maintain real-time processes without enabled content. Interactively mesh quality deliverables without market positioning internal or "organic" sources. Objectively. . .</p>
                                            <hr />
                                            <div class="btm_blk">
                                                <div class="txt">
                                                    <h5>Campaign Management</h5>
                                                    <p>Janurary 2020</p>
                                                </div>
                                                <div class="btn_blk">
                                                    <button class="site_btn simple border"><img src={getSiteImages("images/icon-link.svg")} alt="" /></button>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    :
                    ""
            }
        </section>
    )
}
