import React, { Fragment, useState } from 'react'
import { getSiteImages, getServerImage } from "../../../helpers/api";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
let settings = {
    dots: false,
    infinite: true,
    speed: 1000,
    slidesToShow: 4,
    slidesToScroll: 1,
    autoplaySpeed: 2000,
    autoplay: true
};
export default function Services(props) {
    const [state, setState] = useState({
        nav1: this.slider1,
        nav2: this.slider2,
    });
    console.log(props.services);
    return (
        <section id="serve">
            <div className="contain">
                <div className="top_head">
                    <h2 className="heading">{props.heading}</h2>
                    {/* <div className="btn_blk">
                        <button type="button" className="serve-prev slick-arrow"></button>
                        <button type="button" className="serve-next slick-arrow"></button>
                    </div> */}
                </div>
                <div id="slick-serve" className="slick-carousel">
                    <Slider {...settings} asNavFor={state.nav1} ref={slider => (this.slider1 = slider)}>
                        {
                            (props.services != undefined && props.services.length > 0) ?
                                props.services.map((service, index) => (
                                    <div className="item" key={index}>
                                        <div className="serve_blk" style={{ backgroundImage: "url('" + getServerImage('uploads/services/', service.background, 'thumb_') + "')" }}>
                                            <button type="button" className="play_btn pop_btn" data-popup="video" data-src={service.video}><img src={getSiteImages("images/play_icon.svg")} alt="" /></button>
                                            <div className="in_blk">
                                                <div className="ico fill"><img src={getServerImage('uploads/services/', service.image, 'thumb_')} alt={service.name} /></div>
                                                <div className="txt">
                                                    <h5>{service.name}</h5>
                                                    <p className="small">{service.designation}</p>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                ))
                                :
                                ''
                        }
                    </Slider>
                </div>
            </div >
        </section >
    )
}
