import React, { useEffect, Fragment, useState } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { useForm } from "react-hook-form";
import { getData, getSiteImages, checkPattern, postData } from "./helpers/api";
import './App.css';
import './Main.css';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Footer from "./components/includes/footer";
import Header from "./components/includes/header";
import Home from "./components/pages/home";

function App() {
  const [loading, setLoading] = useState(false);
  const { register, handleSubmit, formState: { errors } } = useForm();
  const [contact, setContact] = useState({
    name: '',
    email: '',
    phone: '',
    comments: ''
  });
  const [publisher, setPublisher] = useState({
    name: '',
    email: '',
    phone: '',
    comments: ''
  });
  const [career, setCareer] = useState({
    name: '',
    email: '',
    phone: '',
    comments: ''
  });
  const [state, setState] = useState({
    careerPopup: false,
    publisherPopup: false,
    contactPopup: false,

  });
  useEffect(() => {
    getData("home").then((data) => {
      setState({
        ...state,
        content: data.content,
        metatags: data.metatags,
        services: data.services,
        casestudies: data.casestudies,
        right_faqs: data.right_faqs,
        left_faqs: data.left_faqs,
        categories: data.categories,
        site_settings: data.site_settings
      });
      setLoading(true);
    });
  }, []);
  function showCareerPopup() {
    setState({ ...state, careerPopup: true });
  }
  function closeCareerPopup() {
    document.getElementById("comments").value = "";
    setCareer({ ...career, name: "", phone: "", email: "", comments: "" });
    setState({ ...state, careerPopup: false });

  }
  function showPublisherPopup() {
    setState({ ...state, publisherPopup: true });
  }
  function closePublisherPopup() {
    document.getElementById("comments").value = "";
    setPublisher({ ...publisher, name: "", phone: "", email: "", comments: "" });
    setState({ ...state, publisherPopup: false });
  }
  function showContactPopup() {
    setState({ ...state, contactPopup: true });
  }
  function closeContactPopup() {
    document.getElementById("comments").value = "";
    setContact({ ...contact, name: "", phone: "", email: "", comments: "" });
    setState({ ...state, contactPopup: false });

  }
  function onSubmit() {

    if (contact.name_error === true || contact.name === '') {
      setContact({ ...contact, name_error: true, name_msg: "Required" });
    }
    else if (contact.email_error === true || contact.email === true) {
      setContact({ ...contact, email_error: true, email_error_msg: "Enter a valid email address" });
    }
    else if (contact.phone_error === true || contact.phone === '') {
      setContact({ ...contact, phone_error: true, phone_msg: "Enter a valid phone number" });
    }
    else if (contact.comment_error === true || contact.comments === '') {
      setContact({ ...contact, comment_error: true, comment_msg: "Required" });
    }
    else {
      setContact({ ...contact, btn_loading: true });
      var form_data = new FormData();

      for (var key in contact) {
        form_data.append(key, contact[key]);
      }
      postData("contact", form_data).then((data) => {
        console.log(data);
        if (data.status == 1) {
          // document.getElementById("frmContact").reset();
          setContact({ ...contact, btn_loading: false, email: "", name: '', phone: '', comments: '' });
          document.getElementById("comments").value = "";
          toast.success(`${data.msg}`, {
            position: "top-right",
            autoClose: 1000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });
        }
        else {
          setContact({ ...contact, btn_loading: false });
          toast.error(`${data.msg}`, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });
        }
      });
    }
  }
  function onCareersSubmit() {

    if (career.name_error === true || career.name === '') {
      setCareer({ ...career, name_error: true, name_msg: "Required" });
    }
    else if (career.email_error === true || career.email === true) {
      setCareer({ ...career, email_error: true, email_error_msg: "Enter a valid email address" });
    }
    else if (career.phone_error === true || career.phone === '') {
      setCareer({ ...career, phone_error: true, phone_msg: "Enter a valid phone number" });
    }
    else if (career.comment_error === true || career.comments === '') {
      setCareer({ ...career, comment_error: true, comment_msg: "Required" });
    }
    else {
      setCareer({ ...career, btn_loading: true });
      var form_data = new FormData();

      for (var key in career) {
        form_data.append(key, career[key]);
      }
      postData("careers", form_data).then((data) => {
        console.log(data);
        if (data.status == 1) {
          document.getElementById("frmCareer").reset();
          setCareer({ ...career, email: "", name: '', phone: '', comments: null });
          setCareer({ ...career, btn_loading: false });
          document.getElementById("comments").value = "";
          toast.success(`${data.msg}`, {
            position: "top-right",
            autoClose: 1000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });
        }
        else {
          setCareer({ ...career, btn_loading: false });
          toast.error(`${data.msg}`, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });
        }
      });
    }
  }
  function onPublisherSubmit() {

    if (publisher.name_error === true || publisher.name === '') {
      setPublisher({ ...publisher, name_error: true, name_msg: "Required" });
    }
    else if (publisher.email_error === true || publisher.email === true) {
      setPublisher({ ...publisher, email_error: true, email_error_msg: "Enter a valid email address" });
    }
    else if (publisher.phone_error === true || publisher.phone === '') {
      setPublisher({ ...publisher, phone_error: true, phone_msg: "Enter a valid phone number" });
    }
    else if (publisher.comment_error === true || publisher.comments === '') {
      setPublisher({ ...publisher, comment_error: true, comment_msg: "Required" });
    }
    else {
      setPublisher({ ...publisher, btn_loading: true });
      var form_data = new FormData();

      for (var key in publisher) {
        form_data.append(key, publisher[key]);
      }
      postData("publisher", form_data).then((data) => {
        console.log(data);
        if (data.status == 1) {
          document.getElementById("frmPublisher").reset();
          setPublisher({ ...publisher, btn_loading: false, email: "", name: '', phone: '', comments: null });
          document.getElementById("comments").value = "";
          toast.success(`${data.msg}`, {
            position: "top-right",
            autoClose: 1000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });
        }
        else {
          setPublisher({ ...publisher, btn_loading: false });
          toast.error(`${data.msg}`, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });
        }
      });
    }
  }
  function handleCareerChange(e) {
    if (e.target.name === 'name') {
      if (e.target.value === "") {
        setCareer({ ...career, [e.target.name]: e.target.value, name_error: true, name_msg: "Required" });
      }
      else {
        setCareer({ ...career, [e.target.name]: e.target.value, name_error: false, name_msg: "" });
      }
    }
    if (e.target.name === 'phone') {
      if (e.target.value === "") {
        setCareer({ ...career, [e.target.name]: e.target.value, phone_error: true, phone_msg: "Required" });
      }
      else {
        if (e.target.value != null || e.target.value != 'null') {
          let mobile_chk = checkPattern(e.target.value, /^[0-9\b]+$/);
          if (mobile_chk === false) {
            setCareer({ ...career, [e.target.name]: e.target.value, phone_error: true, phone_msg: "Invalid phone number" });
          }
          else {
            setCareer({ ...career, [e.target.name]: e.target.value, phone_error: false, phone_msg: "" });
          }
        }

      }
    }
    if (e.target.name === 'email') {

      let email_chk = checkPattern(e.target.value, /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,})+$/);

      if (email_chk === false) {
        if (e.target.value === '') {
          setCareer({ ...career, email_error: false });
        }
        else {
          setCareer({ ...career, [e.target.name]: e.target.value, email_error: true, email_error_msg: "Invalid e-mail address" });
        }

      }
      else {
        setCareer({ ...career, [e.target.name]: e.target.value, email_error: false, email_error_msg: "" });
      }

    }
    if (e.target.name === 'comments') {
      if (e.target.value === "") {
        setCareer({ ...career, [e.target.name]: e.target.value, comment_error: true, comment_msg: "Required" });
      }
      else {
        setCareer({ ...career, [e.target.name]: e.target.value, comment_error: false, comment_msg: "" });
      }
    }
  }
  function handleContactChange(e) {
    if (e.target.name === 'name') {
      if (e.target.value === "") {
        setContact({ ...contact, [e.target.name]: e.target.value, name_error: true, name_msg: "Required" });
      }
      else {
        setContact({ ...contact, [e.target.name]: e.target.value, name_error: false, name_msg: "" });
      }
    }
    if (e.target.name === 'phone') {
      if (e.target.value === "") {
        setContact({ ...contact, [e.target.name]: e.target.value, phone_error: true, phone_msg: "Required" });
      }
      else {
        if (e.target.value != null || e.target.value != 'null') {
          let mobile_chk = checkPattern(e.target.value, /^[0-9\b]+$/);
          if (mobile_chk === false) {
            setContact({ ...contact, [e.target.name]: e.target.value, phone_error: true, phone_msg: "Invalid phone number" });
          }
          else {
            setContact({ ...contact, [e.target.name]: e.target.value, phone_error: false, phone_msg: "" });
          }
        }

      }
    }
    if (e.target.name === 'email') {

      let email_chk = checkPattern(e.target.value, /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,})+$/);

      if (email_chk === false) {
        if (e.target.value === '') {
          setContact({ ...contact, email_error: false });
        }
        else {
          setContact({ ...contact, [e.target.name]: e.target.value, email_error: true, email_error_msg: "Invalid e-mail address" });
        }

      }
      else {
        setContact({ ...contact, [e.target.name]: e.target.value, email_error: false, email_error_msg: "" });
      }

    }
    if (e.target.name === 'comments') {
      if (e.target.value === "") {
        setContact({ ...contact, [e.target.name]: e.target.value, comment_error: true, comment_msg: "Required" });
      }
      else {
        setContact({ ...contact, [e.target.name]: e.target.value, comment_error: false, comment_msg: "" });
      }
    }
  }
  function handlePublisherChange(e) {
    if (e.target.name === 'name') {
      if (e.target.value === "") {
        setPublisher({ ...publisher, [e.target.name]: e.target.value, name_error: true, name_msg: "Required" });
      }
      else {
        setPublisher({ ...publisher, [e.target.name]: e.target.value, name_error: false, name_msg: "" });
      }
    }
    if (e.target.name === 'phone') {
      if (e.target.value === "") {
        setPublisher({ ...publisher, [e.target.name]: e.target.value, phone_error: true, phone_msg: "Required" });
      }
      else {
        if (e.target.value != null || e.target.value != 'null') {
          let mobile_chk = checkPattern(e.target.value, /^[0-9\b]+$/);
          if (mobile_chk === false) {
            setPublisher({ ...publisher, [e.target.name]: e.target.value, phone_error: true, phone_msg: "Invalid phone number" });
          }
          else {
            setPublisher({ ...publisher, [e.target.name]: e.target.value, phone_error: false, phone_msg: "" });
          }
        }

      }
    }
    if (e.target.name === 'email') {

      let email_chk = checkPattern(e.target.value, /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,})+$/);

      if (email_chk === false) {
        if (e.target.value === '') {
          setPublisher({ ...publisher, email_error: false });
        }
        else {
          setPublisher({ ...publisher, [e.target.name]: e.target.value, email_error: true, email_error_msg: "Invalid e-mail address" });
        }

      }
      else {
        setPublisher({ ...publisher, [e.target.name]: e.target.value, email_error: false, email_error_msg: "" });
      }

    }
    if (e.target.name === 'comments') {
      if (e.target.value === "") {
        setPublisher({ ...publisher, [e.target.name]: e.target.value, comment_error: true, comment_msg: "Required" });
      }
      else {
        setPublisher({ ...publisher, [e.target.name]: e.target.value, comment_error: false, comment_msg: "" });
      }
    }
  }
  if (loading === false) return <div id="loading"> <img src={getSiteImages('/images/loading.gif')} alt="Loading" /></div>;
  return (

    <Router>
      <Fragment>
        <Header settings={state.site_settings} showPopup={showCareerPopup} showPublisherPopup={showPublisherPopup} showContactPopup={showContactPopup} />
        <Switch>
          <Route
            exact
            path="/"
            // component={Home}

            render={props => <Home content={state} showPopup={showCareerPopup} showPublisherPopup={showPublisherPopup} showContactPopup={showContactPopup} />}
          />

        </Switch>
        <Footer settings={state.site_settings} />
      </Fragment>
      {
        (state.contactPopup === true)
          ?
          <section className="popup" data-popup="contact" style={{ display: "block" }}>
            <ToastContainer />
            <div className="table_dv">
              <div className="table_cell">
                <div className="contain">
                  <div className="_inner">
                    <button type="button" className="x_btn" onClick={closeContactPopup}></button>
                    <div dangerouslySetInnerHTML={{ __html: state.content.contact_detail }} />
                    <form method="post" onSubmit={handleSubmit(onSubmit)} id="frmContact">
                      <div className="form_row row">
                        <div className="col-xs-12">
                          <h6 className="require">Full Name</h6>
                          <div className="form_blk">
                            <input type="text" name="name" id="name" className="input" placeholder="eg: John Wick" onChange={handleContactChange} />
                            {
                              (contact.name_error === true) ?
                                <p className="error">
                                  <i className="fi-warning"></i> {contact.name_msg}
                                </p>
                                :
                                ""

                            }
                          </div>
                        </div>
                        <div className="col-xs-12">
                          <h6 className="require">Email Address</h6>
                          <div className="form_blk">
                            <input type="text" name="email" id="email" className="input" placeholder="eg: sample@gmail.com" onChange={handleContactChange} />
                            {
                              (contact.email_error === true) ?
                                <p className="error">
                                  <i className="fi-warning"></i> {contact.email_error_msg}
                                </p>
                                :
                                ""

                            }
                          </div>
                        </div>
                        <div className="col-xs-12">
                          <h6 className="require">Phone Number</h6>
                          <div className="form_blk">
                            <input type="text" name="phone" id="phone" className="input" placeholder="eg: 092 1234 567" onChange={handleContactChange} />
                            {
                              (contact.phone_error === true) ?
                                <p className="error">
                                  <i className="fi-warning"></i> {contact.phone_msg}
                                </p>
                                :
                                ""

                            }
