import React, { useState, useEffect } from 'react'
import { getServerImage } from "../../helpers/api";
import {
    Link
} from "react-router-dom";
export default function Header(props) {

    const [scroll, setScroll] = useState(false);
    const [menu, setMenu] = useState(false);
    useEffect(() => {
        window.addEventListener("scroll", () => {
            setScroll(window.scrollY > 50);
        });
    }, []);
    function toggleMenu() {
        setMenu(!menu);
    }
    return (
        <header className={scroll ? "ease fix" : "ease"}>
            <div className="contain">
                <div className="logo">
                    <Link to="/"><img src={getServerImage('uploads/images/', props.settings.site_logo)} alt={props.settings.site_name} /></Link>
                </div>
                <button type="button" className={(menu) ? "toggle active" : "toggle"} onClick={toggleMenu}></button>
                <nav className="ease">
                    <div id="nav" className={(menu) ? 'active' : ''}>
                        <ul id="lst">
                            <li className="">
                                <a href="#serve">Services</a>
                            </li>
                            <li className="">
                                <a href="#why">Why us?</a>
                            </li>
                            <li className="">
                                <a href="#faq">FAQ & Vendors</a>
                            </li>
                            <li className="">
                                <a href="javascript:void(0)" className="pop_btn" data-popup="careers" onClick={props.showPopup}>Careers</a>
                            </li>
                            <li className="">
                                <a href="#become" className="run_btn" onClick={props.showPublisherPopup}>Become a Publisher</a>
                            </li>

                        </ul>
                        <ul id="cta">
                            <li><a href="javascript:void(0)" className="site_btn long pop_btn" data-popup="contact" onClick={props.showContactPopup}>Contact us</a></li>
                        </ul>
                    </div>
                </nav>
                <div className="clearfix"></div>
            </div>
        </header>


    )
}
