import React, { useEffect, Fragment, useState } from "react";
import { getSiteImages, getData, metaTags, getServerImage, checkPattern } from "../../helpers/api";
import Services from './home/services';
import FAQ from './home/faq';
import Collapsible from 'react-collapsible';
import Testimonials from "./home/testimonials";
export default function Career(props) {
    const [loading, setLoading] = useState(false);
    const [career, setCareer] = useState({
        name: '',
        email: '',
        phone: '',
        comments: ''
    });
    const [state, setState] = useState({
        careerPopup: false,
        publisherPopup: false,
        contactPopup: false,

    });
    function onCareersSubmit() {

        if (career.name_error === true || career.name === '') {
            setCareer({ ...career, name_error: true, name_msg: "Required" });
        }
        else if (career.email_error === true || career.email === '') {
            setCareer({ ...career, email_error: true, email_error_msg: "Enter a valid email address" });
        }
        else if (career.phone_error === true || career.phone === '') {
            setCareer({ ...career, phone_error: true, phone_msg: "Enter a valid phone number" });
        }
        else if (career.comment_error === true || career.comments === '') {
            setCareer({ ...career, comment_error: true, comment_msg: "Required" });
        }
        else {
            setCareer({ ...career, btn_loading: true });
            var form_data = new FormData();

            for (var key in career) {
                form_data.append(key, career[key]);
            }
            postData("careers", form_data).then((data) => {
                console.log(data);
                if (data.status == 1) {
                    document.getElementById("frmCareer").reset();
                    setCareer({ ...career, email: "", name: '', phone: '', comments: null });
                    setCareer({ ...career, btn_loading: false });
                    document.getElementById("comments").value = "";
                    toast.success(`${data.msg}`, {
                        position: "top-right",
                        autoClose: 1000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                    });
                }
                else {
                    setCareer({ ...career, btn_loading: false });
                    toast.error(`${data.msg}`, {
                        position: "top-right",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                    });
                }
            });
        }
    }
    function handleCareerChange(e) {
        if (e.target.name === 'name') {
            if (e.target.value === "") {
                setCareer({ ...career, [e.target.name]: e.target.value, name_error: true, name_msg: "Required" });
            }
            else {
                setCareer({ ...career, [e.target.name]: e.target.value, name_error: false, name_msg: "" });
            }
        }
        if (e.target.name === 'phone') {
            if (e.target.value === "") {
                setCareer({ ...career, [e.target.name]: e.target.value, phone_error: true, phone_msg: "Required" });
            }
            else {
                if (e.target.value != null || e.target.value != 'null') {
                    let mobile_chk = checkPattern(e.target.value, /^[0-9\b]+$/);
                    if (mobile_chk === false) {
                        setCareer({ ...career, [e.target.name]: e.target.value, phone_error: true, phone_msg: "Invalid phone number" });
                    }
                    else {
                        setCareer({ ...career, [e.target.name]: e.target.value, phone_error: false, phone_msg: "" });
                    }
                }

            }
        }
        if (e.target.name === 'email') {

            let email_chk = checkPattern(e.target.value, /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,})+$/);

            if (email_chk === false) {
                if (e.target.value === '') {
                    setCareer({ ...career, email_error: false });
                }
                else {
                    setCareer({ ...career, [e.target.name]: e.target.value, email_error: true, email_error_msg: "Invalid e-mail address" });
                }

            }
            else {
                setCareer({ ...career, [e.target.name]: e.target.value, email_error: false, email_error_msg: "" });
            }

        }
        if (e.target.name === 'comments') {
            if (e.target.value === "") {
                setCareer({ ...career, [e.target.name]: e.target.value, comment_error: true, comment_msg: "Required" });
            }
            else {
                setCareer({ ...career, [e.target.name]: e.target.value, comment_error: false, comment_msg: "" });
            }
        }
    }
    useEffect(() => {
        getData("career-page").then((data) => {
            setState({
                ...state,
                content: data.content,
                metatags: data.metatags,
                services: data.services,
                right_faqs: data.right_faqs,
                left_faqs: data.left_faqs,
                site_settings: data.site_settings,
                testimonials: data.testimonials,
                skills: data.skills,
            });
            setLoading(true);
        });
    }, []);
    if (loading === false) return <div id="loading"> <img src={getSiteImages('/images/loading.gif')} alt="Loading" /></div>;
    console.log(state.content);
    return (
        <main>

            {metaTags(state.metatags)}
            <section id="banner" style={{ backgroundImage: "url('" + getSiteImages('images/hero_banner.png') + "')" }}>
                <div className="contain">
                    <div className="flex_box">
                        <div className="content">
                            <div dangerouslySetInnerHTML={{ __html: state.content.banner_detail }} />

                        </div>
                        <div className="img_blk career_blk">
                            <div className="blk">
                                <h3>{state.content.banner_form_text} </h3>
                                <form method="POST" onSubmit={handleSubmit(onCareersSubmit)} id="frmCareer">
                                    <div className="form_row row">
                                        <div className="col-xs-12">
                                            <div className="form_blk">
                                                <input type="text" name="name" id="name" className="input" placeholder="eg: John Wick" onChange={handleCareerChange} />
                                                {
                                                    (career.name_error === true) ?
                                                        <p className="error">
                                                            <i className="fi-warning"></i> {career.name_msg}
                                                        </p>
                                                        :
                                                        ""

                                                }
                                            </div>
                                        </div>
                                        <div className="col-xs-12">
                                            <div className="form_blk">
                                                <input type="text" name="phone" id="phone" className="input" placeholder="eg: 092 1234 567" onChange={handleCareerChange} />
                                                {
                                                    (career.phone_error === true) ?
                                                        <p className="error">
                                                            <i className="fi-warning"></i> {career.phone_msg}
                                                        </p>
                                                        :
                                                        ""

                                                }
                                            </div>
                                        </div>
                                        <div className="col-xs-12">
                                            <div className="form_blk">
                                                <input type="text" name="email" id="email" className="input" placeholder="eg: sample@gmail.com" onChange={handleCareerChange} />
                                                {
                                                    (career.email_error === true) ?
                                                        <p className="error">
                                                            <i className="fi-warning"></i> {career.email_error_msg}
                                                        </p>
                                                        :
                                                        ""

                                                }
                                            </div>
                                        </div>
                                        <div className="col-xs-12">
                                            <div className="form_blk">
                                                <select className="input" name="">
                                                    <option value=''>What's your skill?</option>
                                                    {
                                                        (state.skills != undefined && state.skills.length > 0) ?
                                                            state.skills.map((skill, index) => (
                                                                <option value={skill.name} key={index}>{skill.name}</option>
                                                            ))
                                                            :
                                                            ""
                                                    }
                                                </select>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="btn_blk form_btn text-center">
                                        <button className="site_btn lg long purple">Submit</button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </section>

            <section className="mini_label">
                <div className="contain">
                    <div className="flex">
                        <div className="col">
                            <div className="mini_icon_lbl">
                                <img src={getServerImage('uploads/images/', state.content.image1)} alt={state.content.section1_heading1} />
                            </div>
                            <h4>{state.content.section1_heading1}</h4>
                            <p>{state.content.section1_text1}</p>
                        </div>
                        <div className="col">
                            <div className="mini_icon_lbl">
                                <img src={getServerImage('uploads/images/', state.content.image2)} alt={state.content.section1_heading2} />
                            </div>
                            <h4>{state.content.section1_heading2}</h4>
                            <p>{state.content.section1_text2}</p>
                        </div>
                        <div className="col">
                            <div className="mini_icon_lbl">
                                <img src={getServerImage('uploads/images/', state.content.image3)} alt={state.content.section1_heading3} />
                            </div>
                            <h4>{state.content.section1_heading3}</h4>
                            <p>{state.content.section1_text3}</p>
                        </div>
                    </div>
                </div>
            </section>
            <section className="img_blk_banner">
                <div className="contain contain_full_wide">
                    <div className="flex">
                        <div className="colL">
                            <div className="image_sktech">
                                <img src={getSiteImages("images/sketch1.png")} alt="" />
                            </div>
                        </div>
                        <div className="colR">
                            <div className="image">
                                <img src={getServerImage('uploads/images/', state.content.image4, 'thumb_')} alt={state.content.section2_title} />
                            </div>
                        </div>
                    </div>
                </div>

                <div className="outer_small_blk">
                    <div className="contain">
                        <div className="small_blk">
                            <h1 className="heading">{state.content.section2_title}</h1>
                            <p>{state.content.section2_details}</p>
                        </div>
                    </div>
                </div>

            </section>
            <section className="how_it_work">
                <div className="contain">
                    <div className="content text-center">
                        <h1 className="heading">{state.content.section3_heading}</h1>
                        <p>{state.content.section3_detail}</p>
                    </div>
                    <div className="flex">
                        <div className="colL">
                            <div className="image1">
                                <img src={getServerImage('uploads/images/', state.content.image5, 'thumb_')} alt="" />
                            </div>
                            <div className="image2">
                                <img src={getServerImage('uploads/images/', state.content.image6, 'thumb_')} alt="" />
                            </div>
                        </div>
                        <div className="colR">
                            <div className="inner">
                                <div className="icon_left">
                                    <img src={getServerImage('uploads/images/', state.content.image7)} alt={state.content.section3_heading7} />
                                </div>
                                <div className="cntnt">
                                    <h4>{state.content.section3_heading7}</h4>
                                    <p>{state.content.section3_text7}</p>
                                </div>
                            </div>

                            <div className="inner">
                                <div className="icon_left">
                                    <img src={getServerImage('uploads/images/', state.content.image8)} alt={state.content.section3_heading8} />
                                </div>
                                <div className="cntnt">
                                    <h4>{state.content.section3_heading8}</h4>
                                    <p>{state.content.section3_text8}</p>
                                </div>
                            </div>
                            <div className="inner">
                                <div className="icon_left">
                                    <img src={getServerImage('uploads/images/', state.content.image9)} alt={state.content.section3_heading9} />
                                </div>
                                <div className="cntnt">
                                    <h4>{state.content.section3_heading9}</h4>
                                    <p>{state.content.section3_text9}</p>
                                </div>
                            </div>
                        </div>
                    </div>


                    <div className="btn_blk text-center">
                        <a href={state.content.section3_link_url} className="site_btn lg long purple">{state.content.section3_link_text}</a>
                    </div>
                </div>
            </section>
            <Services heading={state.content.section4_heading} services={state.services} page="career" />



            <FAQ heading={state.content.section5_heading} left_faqs={state.left_faqs} right_faqs={state.right_faqs} page="career" />

            <Testimonials heading={state.content.section6_heading} testimonials={state.testimonials} image1={state.content.image10} image2={state.content.image11} link_text={state.content.section6_link_text} link_url={state.content.section6_link_url} details={state.content.section6_detail} />
        </main>
    )
}
