import React, { Fragment, useState } from 'react'
import { getSiteImages, getServerImage, getServerVideo } from "../../../helpers/api";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "../../../../node_modules/video-react/dist/video-react.css";
import { Player } from 'video-react';

import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';
const options = {
    margin: 0,
    responsiveClass: true,
    nav: true,
    dots: false,
    items: 1,
    loop: true,
    autoplay: true,
    smartSpeed: 1000,
    autoHeight: true,
    autoplayTimeout: 2000,
    autoplayHoverPause: true,
    // touchDrag: false,
    responsive: {
        0: {
            items: 1,
        },
        1030: {
            items: 1,

        },
        992: {
            items: 1,

        },
        580: {
            items: 1,
        },

    },
};
let settings = {
    dots: false,
    arrows: true,
    infinite: true,
    autoplay: true,
    autoplaySpeed: 4000,
    speed: 4000,
    variableWidth: true,
    slidesToShow: 4,
    slidesToScroll: 1,
    adaptiveHeight: true,
    responsive: [
        {
            breakpoint: 1030,
            settings: {
                slidesToShow: 3,
            },
        },
        {
            breakpoint: 992,
            settings: {
                slidesToShow: 2,
            },
        },
        {
            breakpoint: 580,
            settings: {
                slidesToShow: 1,
            },
        },
    ],
};
export default function Testimonials(props) {

    const [state, setState] = useState({
        videoPopup: false,
        videoId: null,
        service_title: null
    });
    function showVideoPopup(service) {
        // let videoId = getYoutubeVideoId(service.video);
        setState({ ...state, videoId: service.video, videoPopup: true, service_title: service.name });
    }
    function closePopup() {
        setState({ ...state, videoId: null, videoPopup: false, service_title: false });
    }
    return (
        <section class="testimonial_sec">
            <div class="contain">
                <div class="flex">
                    <div class="colL">
                        <div class="image_small">
                            <img src={getSiteImages("images/testi1.PNG")} alt="" />
                        </div>

                    </div>
                    <div class="colM">
                        <div class="content text-center">
                            <h2>Testimonials</h2>
                            <p>We’re proud to have helped over 200,000 small and medium-sized businesses reach new heights.</p>
                        </div>
                        {/* <div id="testimonial" class="owl-carousel owl-theme"> */}
                        <OwlCarousel className="owl-theme" id="testimonial"  {...options}>
                            <div class="item">
                                <div class="item_inner">
                                    <div class="image_testi_icon">
                                        <img src={getSiteImages("images/01.jpg")} alt="" />
                                    </div>
                                    <div class="cntnt">
                                        <p>“Lumicc really helped me get my business off the ground. I was able to determine my target market and reach them effectively with Lumicc. This is invaluable for any small business owner.”</p>
                                        <h5>Richard Craig</h5>
                                        <div class="review_start">
                                            <img src={getSiteImages("images/stars.png")} alt="" />
                                        </div>
                                    </div>
                                </div>

                            </div>
                        </OwlCarousel>
                        {/* </div> */}
                    </div>
                    <div class="colR">
                        <div class="image_small">
                            <img src={getSiteImages("images/testi2.PNG")} alt="" />
                        </div>
                    </div>
                </div>
                <div class="btn_blk text-center">
                    <a href="" class="site_btn lg long purple">Get started</a>
                </div>
            </div>
        </section>
    )
}
