import React, { useEffect, Fragment, useState } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { useForm } from "react-hook-form";
import { getData, getSiteImages, checkPattern, postData } from "./helpers/api";
import './App.css';
import './Main.css';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Footer from "./components/includes/footer";
import Header from "./components/includes/header";
import Home from "./components/pages/home";

function App() {
  const [loading, setLoading] = useState(false);
  const { register, handleSubmit, formState: { errors } } = useForm();
  const [state, setState] = useState({
    careerPopup: false,
    publisherPopup: false,
    contactPopup: false,
    name: '',
    email: '',
    phone: '',
    comments: ''
  });
  useEffect(() => {
    getData("home").then((data) => {
      setState({
        ...state,
        content: data.content,
        metatags: data.metatags,
        services: data.services,
        casestudies: data.casestudies,
        right_faqs: data.right_faqs,
        left_faqs: data.left_faqs,
        categories: data.categories,
        site_settings: data.site_settings
      });
      setLoading(true);
    });
  }, []);
  function showCareerPopup() {
    setState({ ...state, careerPopup: true, name: "", phone: "", email: "", comments: "" });
  }
  function closeCareerPopup() {
    document.getElementById("comments").value = "";
    setState({ ...state, careerPopup: false, name: "", phone: "", email: "", comments: "" });
  }
  function showPublisherPopup() {
    setState({ ...state, publisherPopup: true, name: "", phone: "", email: "", comments: "" });
  }
  function closePublisherPopup() {
    document.getElementById("comments").value = "";
    setState({ ...state, publisherPopup: false, name: "", phone: "", email: "", comments: "" });
  }
  function showContactPopup() {
    setState({ ...state, contactPopup: true, name: "", phone: "", email: "", comments: "" });
  }
  function closeContactPopup() {
    document.getElementById("comments").value = "";
    setState({ ...state, contactPopup: false, name: "", phone: "", email: "", comments: "" });
  }
  function onSubmit() {

    if (state.email_error === true) {
      setState({ ...state, email_error: true });
    }
    else if (state.phone_error === true) {
      setState({ ...state, phone_error: true });
    }
    else {
      setState({ ...state, btn_loading: true });
      var form_data = new FormData();

      for (var key in state) {
        form_data.append(key, state[key]);
      }
      postData("contact", form_data).then((data) => {
        console.log(data);
        if (data.status == 1) {
          setState({ ...state, btn_loading: false, email: "", name: '', phone: '', comments: null });
          document.getElementById("comments").value = "";
          toast.success(`${data.msg}`, {
            position: "top-right",
            autoClose: 1000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });
        }
        else {
          setState({ ...state, btn_loading: false });
          toast.error(`${data.msg}`, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });
        }
      });
    }
  }
  function onCareersSubmit() {

    if (state.email_error === true) {
      setState({ ...state, email_error: true });
    }
    else if (state.phone_error === true) {
      setState({ ...state, phone_error: true });
    }
    else {
      setState({ ...state, btn_loading: true });
      var form_data = new FormData();

      for (var key in state) {
        form_data.append(key, state[key]);
      }
      postData("careers", form_data).then((data) => {
        console.log(data);
        if (data.status == 1) {
          setState({ ...state, btn_loading: false, email: "", name: '', phone: '', comments: null });
          document.getElementById("comments").value = "";
          toast.success(`${data.msg}`, {
            position: "top-right",
            autoClose: 1000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });
        }
        else {
          setState({ ...state, btn_loading: false });
          toast.error(`${data.msg}`, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });
        }
      });
    }
  }
  function onPublisherSubmit() {

    if (state.email_error === true) {
      setState({ ...state, email_error: true });
    }
    else if (state.phone_error === true) {
      setState({ ...state, phone_error: true });
    }
    else {
      setState({ ...state, btn_loading: true });
      var form_data = new FormData();

      for (var key in state) {
        form_data.append(key, state[key]);
      }
      postData("publisher", form_data).then((data) => {
        console.log(data);
        if (data.status == 1) {
          setState({ ...state, btn_loading: false, email: "", name: '', phone: '', comments: null });
          document.getElementById("comments").value = "";
          toast.success(`${data.msg}`, {
            position: "top-right",
            autoClose: 1000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });
        }
        else {
          setState({ ...state, btn_loading: false });
          toast.error(`${data.msg}`, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });
        }
      });
    }
  }
  function handleChange(e) {
    if (e.target.name === 'name') {
      if (e.target.value === "") {
        setState({ ...state, [e.target.name]: e.target.value, name_error: true, name_msg: "Required" });
      }
      else {
        setState({ ...state, [e.target.name]: e.target.value, name_error: false, name_msg: "" });
      }
    }
    if (e.target.name === 'phone') {
      if (e.target.value === "") {
        setState({ ...state, [e.target.name]: e.target.value, phone_error: true, phone_msg: "Required" });
      }
      else {
        if (e.target.value != null || e.target.value != 'null') {
          let mobile_chk = checkPattern(e.target.value, /^[0-9\b]+$/);
          if (mobile_chk === false) {
            setState({ ...state, [e.target.name]: e.target.value, phone_error: true, phone_msg: "Invalid phone number" });
          }
          else {
            setState({ ...state, [e.target.name]: e.target.value, phone_error: false, phone_msg: "" });
          }
        }

      }
    }
    if (e.target.name === 'email') {

      let email_chk = checkPattern(e.target.value, /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,})+$/);

      if (email_chk === false) {
        if (e.target.value === '') {
          setState({ ...state, email_error: false });
        }
        else {
          setState({ ...state, [e.target.name]: e.target.value, email_error: true, email_error_msg: "Invalid e-mail address" });
        }

      }
      else {
        setState({ ...state, [e.target.name]: e.target.value, email_error: false, email_error_msg: "" });
      }

    }
    if (e.target.name === 'comments') {
      if (e.target.value === "") {
        setState({ ...state, [e.target.name]: e.target.value, comment_error: true, comment_msg: "Required" });
      }
      else {
        setState({ ...state, [e.target.name]: e.target.value, comment_error: false, comment_msg: "" });
      }
    }
  }
  if (loading === false) return <div id="loading"> <img src={getSiteImages('/images/loading.gif')} alt="Loading" /></div>;
  return (

    <Router>
      <Fragment>
        <Header settings={state.site_settings} showPopup={showCareerPopup} showPublisherPopup={showPublisherPopup} showContactPopup={showContactPopup} />
        <Switch>
          <Route
            exact
            path="/"
            // component={Home}

            render={props => <Home content={state} showPopup={showCareerPopup} showPublisherPopup={showPublisherPopup} showContactPopup={showContactPopup} />}
          />

        </Switch>
        <Footer settings={state.site_settings} />
      </Fragment>
      {
        (state.contactPopup === true)
          ?
          <section className="popup" data-popup="contact" style={{ display: "block" }}>
            <ToastContainer />
            <div className="table_dv">
              <div className="table_cell">
                <div className="contain">
                  <div className="_inner">
                    <button type="button" className="x_btn" onClick={closeContactPopup}></button>
                    <h4>Contact us</h4>
                    <p>Have questions? Looking for answers? Found none? Get in touch with us. We'd love to help you out!</p>
                    <form method="post" onSubmit={handleSubmit(onSubmit)}>
                      <div className="form_row row">
                        <div className="col-xs-12">
                          <h6 className="require">Full Name</h6>
                          <div className="form_blk">
                            <input type="text" name="name" id="name" className="input" placeholder="eg: John Wick" {...register("name", {
                              required: "Required",
                            })} onChange={handleChange} value={state.name} />
                          </div>
                        </div>
                        <div className="col-xs-12">
                          <h6 className="require">Email Address</h6>
                          <div className="form_blk">
                            <input type="text" name="email" id="email" className="input" placeholder="eg: sample@gmail.com" {...register("email", {
                              required: "Required",
                            })} onChange={handleChange} value={state.email} />
                            {
                              (state.email_error === true) ?
                                <p className="error">
                                  <i className="fi-warning"></i> {state.email_error_msg}
                                </p>
                                :
                                ""

                            }
                          </div>
                        </div>
                        <div className="col-xs-12">
                          <h6 className="require">Phone Number</h6>
                          <div className="form_blk">
                            <input type="text" name="phone" id="phone" className="input" placeholder="eg: 092 1234 567" {...register("phone", {
                              required: "Required",
                            })} onChange={handleChange} value={state.phone} />
                            {
                              (state.phone_error === true) ?
                                <p className="error">
                                  <i className="fi-warning"></i> {state.phone_msg}
                                </p>
                                :
                                ""

                            }
                          </div>
                        </div>
                        <div className="col-xs-12">
                          <h6>Comments</h6>
                          <div className="form_blk">
                            <textarea name="comments" id="comments" className="input" placeholder="How can we help?" {...register("comments", {
                              required: "Required",
                            })} onChange={handleChange}>{state.comments}</textarea>
                          </div>
                        </div>
                      </div>
                      <div className="btn_blk form_btn text-center">
                        <button className="site_btn lg long purple" disabled={(state.btn_loading === true) ? 'disabled' : ''}>Submit {(state.btn_loading === true) ? <i className="spinner"></i> : ''}</button>
                      </div>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </section>
          :
          ""
      }
      {
        (state.publisherPopup === true)
          ?
          <section className="popup" data-popup="become" style={{ display: "block" }}>
            <ToastContainer />
            <div className="table_dv">
              <div className="table_cell">
                <div className="contain">
                  <div className="_inner">
