import React, { Fragment, useState } from 'react'
import { getSiteImages, getServerImage, getYoutubeVideoId } from "../../../helpers/api";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
let settings = {
    dots: false,
    infinite: true,
    speed: 1000,
    slidesToShow: 4,
    slidesToScroll: 1,
    autoplaySpeed: 2000,
    autoplay: true,
    adaptiveHeight: true
};
export default function Services(props) {
    function showVideoPopup(video) {
        let videoId = getYoutubeVideoId(video);
        setState({ ...state, videoId: videoId, videoPopup: true });
    }
    const [state, setState] = useState({
        videoPopup: false,
        videoId: null
    });
    function closePopup() {
        setState({ ...state, videoId: null, videoPopup: false });
    }
    return (
        <section id="serve">
            <div className="contain">
                <div className="top_head">
                    <h2 className="heading">{props.heading}</h2>
                    {/* <div className="btn_blk">
                        <button type="button" className="serve-prev slick-arrow"></button>
                        <button type="button" className="serve-next slick-arrow"></button>
                    </div> */}
                </div>
                <div id="slick-serve" className="slick-carousel">
                    <Slider {...settings}>
                        {
                            (props.services != undefined && props.services.length > 0) ?
                                props.services.map((service, index) => (
                                    <div className="item" key={index}>
                                        <div className="serve_blk" style={{ backgroundImage: "url('" + getServerImage('uploads/services/', service.background, 'thumb_') + "')" }}>
                                            <button type="button" className="play_btn pop_btn" data-popup="video" data-src={service.video} onClick={() => showVideoPopup(service.video)}><img src={getSiteImages("images/play_icon.svg")} alt="" /></button>
                                            <div className="in_blk">
                                                <div className="ico fill"><img src={getServerImage('uploads/services/', service.image, 'thumb_')} alt={service.name} /></div>
                                                <div className="txt">
                                                    <h5>{service.name}</h5>
                                                    <p className="small">{service.designation}</p>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                ))
                                :
                                ''
                        }
                    </Slider>
                </div>
            </div >
            {
                (state.videoPopup === true && state.videoId != null && state.videoId != undefined && state.videoId != '')
                    ?
                    <section className="popup lg" data-popup="video" style={{ display: "block" }}>
                        <div className="table_dv">
                            <div className="table_cell">
                                <div className="contain">
                                    <div className="_inner">
                                        <button type="button" className="x_btn" onClick={closePopup}></button>
                                        <h4>Lorem ipsum dolor, sit amet.</h4>
                                        <div id="vid_blk" className="vid_blk">
                                            <iframe src={"//www.youtube.com/embed/" + state.videoId} frameBorder="0" allowFullScreen></iframe>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </section>
                    :
                    ""
            }
        </section >
    )
}
