import React, { Fragment } from 'react'
import { getSiteImages } from "../../../helpers/api";

export default function FAQ(props) {
    function expandFaq(e) {
        e.stopPropagation();
        let elements = document.querySelectorAll(".faq_lst .faq_blk");
        let currentElement = e.currentTarget;
        if (currentElement.classList.contains("active")) {
            for (var i = 0; i < elements.length; i++) {
                elements[i].classList.remove("active");
            }
        } else {
            for (var j = 0; j < elements.length; j++) {
                elements[j].classList.remove("active");
            }
            currentElement.classList.add("active");
        }
    }
    return (
        <section id="faq">
            <div className="contain">
                <div className="content text-center">
                    <h2>{props.heading}</h2>
                </div>
                <div className="flex_row">
                    <div className="col">
                        <div className="faq_lst">
                            {
                                (props.left_faqs != undefined && props.left_faqs.length > 0) ?
                                    props.left_faqs.map((left_faq, index) => (
                                        <div className={index === 0 ? "faq_blk active" : "faq_blk"} onClick={(e) => expandFaq(e)} key={index}>
                                            <h6>{left_faq.question}</h6>
                                            <div className="txt">
                                                <div dangerouslySetInnerHTML={{ __html: left_faq.answer }} />
                                            </div>
                                        </div>
                                    ))
                                    :
                                    ""
                            }
                        </div>
                    </div>
                    <div className="col">
                        <div className="faq_lst">
                            <div className="faq_blk" onClick={(e) => expandFaq(e)}>
                                <h6>Will I be assigned an account manager?</h6>
                                <div className="txt">
                                    <p>Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam nonumy eirmod tempor invidunt ut labore et dolore magna aliquyam erat, sed diam voluptua.</p>
                                </div>
                            </div>
                            <div className="faq_blk" onClick={(e) => expandFaq(e)}>
                                <h6>How can I measure the success of a campaign?</h6>
                                <div className="txt">
                                    <p>Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam nonumy eirmod tempor invidunt ut labore et dolore magna aliquyam erat, sed diam voluptua.</p>
                                </div>
                            </div>
                            <div className="faq_blk" onClick={(e) => expandFaq(e)}>
                                <h6>What is the most effective ad format?</h6>
                                <div className="txt">
                                    <p>Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam nonumy eirmod tempor invidunt ut labore et dolore magna aliquyam erat, sed diam voluptua.</p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    )
}
