import React, { useEffect, Fragment, useState } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { getData, getSiteImages } from "./helpers/api";
import './App.css';
import './Main.css';
import Footer from "./components/includes/footer";
import Header from "./components/includes/header";
import Home from "./components/pages/home";

function App() {
  const [loading, setLoading] = useState(false);
  const [state, setState] = useState({

  });
  useEffect(() => {
    getData("home").then((data) => {
      setState({
        ...state,
        content: data.content,
        metatags: data.metatags,
        services: data.services,
        casestudies: data.casestudies,
        right_faqs: data.right_faqs,
        left_faqs: data.left_faqs,
        categories: data.categories,
        site_settings: data.site_settings
      });
      setLoading(true);
    });
  }, []);
  if (loading === false) return <div id="loading"> <img src={getSiteImages('/images/loading.gif')} alt="Loading" /></div>;
  return (

    <Router>
      <Header settings={state.site_settings} />
      <Switch>
        <Home content={state} />
      </Switch>
      <Footer settings={state.site_settings} />
    </Router>
  );
}

export default App;
