import React, { useState, useEffect } from 'react'
import { getSiteImages, getServerImage } from "../../helpers/api";
import {
    Link
} from "react-router-dom";
export default function Header(props) {
    console.log(props.settings);
    const [scroll, setScroll] = useState(false);
    useEffect(() => {
        window.addEventListener("scroll", () => {
            setScroll(window.scrollY > 50);
        });
    }, []);
    return (
        <header className={scroll ? "ease fix" : "ease"}>
            <div className="contain">
                <div className="logo">
                    <Link to="/">
                        <img src={getServerImage('uploads/images/', props.settings.site_logo)} alt={props._settings.site_name} />
                    </Link>
                </div>
                <button type="button" className="toggle"></button>
                <nav className="ease">
                    <div id="nav">
                        <ul id="lst">
                            <li className="">
                                <a href="javascript:void(0)" className="pop_btn" data-popup="careers">Careers</a>
                            </li>
                            <li className="">
                                <a href="#become" className="run_btn">Become a Publisher</a>
                            </li>
                            <li className="">
                                <a href="javascript:void(0)" className="pop_btn" data-popup="video">How it works?</a>
                            </li>
                        </ul>
                        <ul id="cta">
                            <li><a href="javascript:void(0)" className="site_btn long pop_btn" data-popup="contact">Contact us</a></li>
                        </ul>
                    </div>
                </nav>
                <div className="clearfix"></div>
            </div>
        </header>


    )
}
